[CmdletBinding()]
param(
    [Parameter()]
    [string]$EnvironmentUrl,
    [string]$Username,
    [string]$PasswordSecret,
    [string]$AppId,
    [string]$ClientSecret,
    [string]$TenantId,
    [string]$Cloud
)

function Invoke-WhoAmI {
    [CmdletBinding()]
    param (
        [parameter (Mandatory = $true)][Hashtable]$authInfo
    )

    begin {
        #Setup parameter hash table
        $Parameters = . Get-ParameterValue
    }

    process {
        $output = Get-Who @Parameters
    }

    end {
        return $output
    }
}

try {
    [Net.ServicePointManager]::SecurityProtocol = [Net.SecurityProtocolType]::Tls12

    if (Get-module -name GitHubActions) {
        ## Make sure the GH Actions module is installed from the Gallery
        Remove-Module GitHubActions -Force -ErrorAction SilentlyContinue
    }
    
    ("..\ps_modules\VstsTaskSdk", "..\ps_modules\SharedFunctions.psm1", "..\ps_modules\Get-ParameterValue.ps1") `
        | %{ Join-Path -Path $PSScriptRoot $_ } | Import-Module
    Write-Information "Imported base modules"
    $redirector = Get-BindingRedirector
    Write-Information "Importing Microsoft.Xrm.WebApi.PowerShell...."
    Import-PowerPlatformToolsPowerShellModule -ModuleName "Microsoft.Xrm.WebApi.PowerShell"
    Write-Information "Gathering Credentials...."
    # Get input parameters and credentials
    $authInfo = '' 

    $PSCredential = New-Object System.Management.Automation.PSCredential ($Username, (ConvertTo-SecureString $PasswordSecret -AsPlainText -Force))
    #if ($selectedAuthName -eq "PowerPlatformEnvironment") {
    if(-not ($ClientSecret -eq "")){
         $authInfo = @{
            EnvironmentUrl  = $EnvironmentUrl
            Credential      = $PSCredential
            TenantId        = $null
            AuthType        = 'OAuth'
        }
    }
    #} elseif ($selectedAuthName -eq "PowerPlatformSPN") {
    else{
        $authInfo = @{
            EnvironmentUrl  = $EnvironmentUrl
            Credential      = $PSCredential
            TenantId        = $TenantId
            AuthType        = 'ClientSecret'
        }
    }
    Write-Information "AuthLog AuthInfo...."
    Write-AuthLog -AuthInfo $authInfo
    Write-Information "Invoke-WhoAmI...."
    Invoke-WhoAmI $authInfo
} 
catch{
    Write-Host $Error
}

finally {
    
    if ($null -ne $redirector) {
        $redirector.Dispose()
    }
}

